import "./App.css";
import { useEffect, useState } from "react";

const url = "https://fake-rest-api-nodejs.herokuapp.com/products";

function Search({ setSearchInput, onSubmitSearch }) {
  return (
    <form className="mb-3 d-flex" onSubmit={(e) => onSubmitSearch(e)}>
      <input
        type="text"
        className="form-control"
        placeholder="Search"
        onChange={(e) => setSearchInput(e.target.value)}
      />
      <button type="submit" className="btn btn-primary">
        Search
      </button>
    </form>
  );
}

function Sort({ setSortInput }) {
  return (
    <select
      className="form-select"
      required
      onChange={(e) => setSortInput(e.target.value)}
    >
      {/* <option selected disabled value="">
      Sort
    </option> */}
      <option>Default</option>
      <option>Price descending</option>
      <option>Price ascending</option>
    </select>
  );
}

function Table({ products }) {
  return (
    <table className="table table-striped table-hover">
      <thead>
        <tr>
          <th scope="col">#</th>
          <th scope="col">Name</th>
          <th scope="col">Price</th>
          <th scope="col">Quantity</th>
          <th scope="col">Image</th>
        </tr>
      </thead>
      <tbody>
        {products.map((product) => (
          <tr key={product.id}>
            <th scope="row">{product.id}</th>
            <td>{product.name}</td>
            <td>{product.price}</td>
            <td>{product.quantity}</td>
            <td>
              <img src={product.thumbnail} alt="thumbnail" />
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

function App() {
  const [products, setProducts] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [sortInput, setSortInput] = useState("");

  //get data
  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
      })
      .catch((error) => console.log(error));
  }, []);

  //handle sort
  useEffect(() => {
    let sortUrl =
      sortInput === "Price descending"
        ? "?_sort=name&_order=desc"
        : sortInput === "Price ascending"
        ? "?_sort=name&_order=asc"
        : "";

    fetch(url + sortUrl)
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
      })
      .catch((error) => console.log(error));
  }, [sortInput]);

  function onSubmitSearch(e) {
    e.preventDefault();
    let searchUrl = "?q=";
    fetch(url + searchUrl + searchInput)
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
      })
      .catch((error) => console.log(error));
  }

  return (
    <div className="App">
      <div className="container">
        <h1 className="text-center mb-4">Product List</h1>

        <div className="row">
          <div className="col-4">
            <Search
              onSubmitSearch={onSubmitSearch}
              searchInput={setSearchInput}
            />
          </div>
          <div className="col-3"></div>
          <div className="col-4">
            <Sort setSortInput={setSortInput} />
          </div>
        </div>

        <Table products={products} />
      </div>
    </div>
  );
}

export default App;
