import './App.css';
import rock from './rock.png';
import paper from './paper.png';
import scissors from './scissors.png';
import { useState } from 'react';


function App() {
  const [player, setPlayer] = useState("");

  function click(e) {

    console.log(e.target.src);
  }

  return (
    <div className="App">
      <h1 className="status">WIN</h1>
      <div className="container">

        <div className="d-flex justify-content-between">
          <div className="player">
            <p className="player-name">you</p>
            <img src={rock} alt="" className="player-choice"></img>
          </div>
          <div className="player">
            <p className="player-name">computer</p>
            <img src={rock} alt="" className="player-choice"></img>
          </div>
        </div>

        <div className="d-flex justify-content-between">
          <img src={rock} alt="" className="player-choice" onClick={click}></img>
          <img src={paper} alt="" className="player-choice" onClick={click}></img>
          <img src={scissors} alt="" className="player-choice" onClick={click}></img>
        </div>
      </div>

    </div>
  );
}

export default App;
