import "./App.css";
import { useEffect, useState } from "react";

function App() {
  const [products, setProducts] = useState([]);
  const [searchInput, setSearchInput] = useState("");

  useEffect(() => {
    fetch("https://fake-rest-api-nodejs.herokuapp.com/products")
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
      })
      .catch((error) => console.log(error));
  }, []);

  function onChangeSearch(e) {
    let newArr = [...products];
    e.target.value === ""
      ? setProducts(newArr)
      : setSearchInput(e.target.value);
  }

  function onSubmitSearch(e) {
    e.preventDefault();
    let newArr = products.filter((product) => {
      return product.name.includes(searchInput);
    });
    setProducts(newArr);
  }

  function onChangeSort(e) {
    let sortInput = e.target.value
    let newArr = [...products];
    if (e.target.value === "Price descending") {
      newArr.sort((a, b) => a.price - b.price);
    } else if (e.target.value === "Price ascending") {
      newArr.sort((a, b) => b.price - a.price);
    } else if (e.target.value === "Default") {
      newArr.sort((a, b) => a.id - b.id);
    }
    setProducts(newArr);
  }

  return (
    <div className="App">
      <div className="container">
        <h1 className="text-center mb-4">Product List</h1>

        <div className="row">
          <div className="col-4">
            <form className="mb-3 d-flex" onSubmit={(e) => onSubmitSearch(e)}>
              <input
                type="text"
                className="form-control"
                placeholder="Search"
                onChange={(e) => onChangeSearch(e)}
              />
              <button type="submit" className="btn btn-primary">
                Search
              </button>
            </form>
          </div>
          <div className="col-3"></div>
          <div className="col-4">
            <select
              className="form-select"
              required
              onChange={(e) => onChangeSort(e)}
            >
              {/* <option selected disabled value="">
                  Sort
                </option> */}
              <option>Default</option>
              <option>Price descending</option>
              <option>Price ascending</option>
            </select>
          </div>
        </div>

        <table className="table table-striped table-hover">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Price</th>
              <th scope="col">Quantity</th>
              <th scope="col">Image</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.id}>
                <th scope="row">{product.id}</th>
                <td>{product.name}</td>
                <td>{product.price}</td>
                <td>{product.quantity}</td>
                <td>
                  <img src={product.thumbnail} alt="thumbnail" />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
