import "./App.css";
import { useEffect, useState } from "react";

const url = "https://fake-rest-api-nodejs.herokuapp.com/products";

function Search({ setSearchInput, onSubmitSearch }) {
  <form className="mb-3 d-flex" onSubmit={(e) => onSubmitSearch(e)}>
    <input
      type="text"
      className="form-control"
      placeholder="Search"
      onChange={(e) => setSearchInput(e.target.value)}
    />
    <button type="submit" className="btn btn-primary">
      Search
    </button>
  </form>;
}

function Sort({ setSortInput }) {
  <select
    className="form-select"
    required
    onChange={(e) => setSortInput(e.target.value)}
  >
    {/* <option selected disabled value="">
      Sort
    </option> */}
    <option>Default</option>
    <option>Price descending</option>
    <option>Price ascending</option>
  </select>;
}

function Table() {}

function App() {
  const [products, setProducts] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [sortInput, setSortInput] = useState("");

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
      })
      .catch((error) => console.log(error));
  }, []);

  useEffect(() => {
    //handle sort
    let sortUrl =
      sortInput === "Price descending"
        ? "?_sort=name&_order=desc"
        : sortInput === "Price ascending"
        ? "?_sort=name&_order=asc"
        : "";

    fetch(url + sortUrl)
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
      })
      .catch((error) => console.log(error));
  }, [sortInput]);

  function onSubmitSearch(e) {
    e.preventDefault();
    let searchUrl = "?q=";
    fetch(url + searchUrl + searchInput)
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
      })
      .catch((error) => console.log(error));
  }

  return (
    <div className="App">
      <div className="container">
        <h1 className="text-center mb-4">Product List</h1>

        <div className="row">
          <div className="col-4">
            <Search
              onSubmitSearch={onSubmitSearch}
              searchInput={setSearchInput}
            />
          </div>
          <div className="col-3"></div>
          <div className="col-4">
            <Sort setSortInput={setSortInput} />
          </div>
        </div>

        <Table products={products}/>

      </div>
    </div>
  );
}

export default App;
