import './App.css';
import rock from './rock.png';
import paper from './paper.png';
import scissors from './scissors.png';
import { useState } from 'react';
let choices = [rock, paper ,scissors]


function App() {
  const [player, setPlayer] = useState("");
  const [computer, setComputer] = useState("");
  const [status, setStatus] = useState("");

  function click(e) {
    setPlayer(e.target.src);
    setComputer()
  }

  return choices.map((choice) => {
    <div className="App">
      <h1 className="status">WIN</h1>
      <div className="container">

        <div className="d-flex justify-content-between mt-5">
          <div className="player">
            <p className="player-name">you</p>
            <img src={player} alt="" className="player-choice"></img>
          </div>
          <div className="player">
            <p className="player-name">computer</p>
            <img src={computer} alt="" className="player-choice"></img>
          </div>
        </div>

        <div className="d-flex justify-content-between mt-5">
          <img src={rock} alt="" className="player-choice" onClick={click}></img>
          <img src={paper} alt="" className="player-choice" onClick={click}></img>
          <img src={scissors} alt="" className="player-choice" onClick={click}></img>
        </div>
      </div>

    </div>
  });
}

export default App;
