import './App.css';
import rock from './rock.png';
import paper from './paper.png';
import scissors from './scissors.png';
import { useState } from 'react';

var myModal = document.getElementById('myModal')
var myInput = document.getElementById('myInput')

myModal.addEventListener('shown.bs.modal', function () {
  myInput.focus()
})

function App() {
  const [player, setPlayer] = useState(scissors);
  const [computer, setComputer] = useState(scissors);
  const [status, setStatus] = useState("Welcome");
  const [win, setWin] = useState(0);
  const [draw, setDraw] = useState(0);
  const [lose, setLose] = useState(0);

  function click(playerRoll) {
    if (playerRoll === 1) {
      setPlayer(rock);
    }
    if (playerRoll === 2) {
      setPlayer(paper);
    }
    if (playerRoll === 3) {
      setPlayer(scissors);
    }

    let computerRoll = Math.floor(Math.random() * 3 + 1);

    if (computerRoll === 1) {
      setComputer(rock)
    }
    if (computerRoll === 2) {
      setComputer(paper)
    }
    if (computerRoll  === 3) {
      setComputer(scissors)
    }

    if (playerRoll === computerRoll) {
      setStatus("DRAW");
      setDraw(draw + 1);
    }
    else if (playerRoll === 1 && computerRoll === 2) {
      setStatus("LOSE");
      setLose(lose + 1);
    }
    else if (playerRoll === 1 && computerRoll === 3) {
      setStatus("WIN");
      setWin(win + 1);
    }
    else if (playerRoll === 2 && computerRoll === 1) {
      setStatus("WIN");
      setWin(win + 1);
    }
    else if (playerRoll === 2 && computerRoll === 3) {
      setStatus("LOSE");
      setLose(lose + 1);
    }
    else if (playerRoll === 3 && computerRoll === 1) {
      setStatus("LOSE");
      setLose(lose + 1);
    }
    else if (playerRoll === 3 && computerRoll === 2) {
      setStatus("WIN");
      setWin(win + 1);
    }

    let total = win + lose + draw;
    if (total === 10) {
      if (win === lose) {
        alert("Draw")
      }
      else if (win > lose) {
        alert("You win")
      }
      else alert("You lose")
      setWin(0);
      setLose(0);
      setDraw(0);
    }

  }

  let reset = () => {
    setWin(0);
    setLose(0);
    setDraw(0);
  }
  return (
    <div className="App">
      <h1 className="status">{status}</h1>
      <div className="container">
        <div className="d-flex justify-content-between play">
          <div className="player me-5">
            <h4 className="player-name ">You</h4>
            <img src={player} alt="" className="player-choice"></img>
          </div>
          <div className="player">
            <h4 className="player-name">Computer</h4>
            <img src={computer} alt="" className="computer-choice"></img>
          </div>
        </div>
        <div className="d-flex justify-content-between mt-5">
          <img src={rock} alt="" className="choices" onClick={() => click(1)}></img>
          <img src={paper} alt="" className="choices" onClick={() => click(2)}></img>
          <img src={scissors} alt="" className="choices" onClick={() => click(3)}></img>
        </div>
        <div className="d-flex justify-content-between mt-5">
          <p className="score">Win: {win}</p>
          <p className="score">Draw: {draw}</p>
          <p className="score">Lose: {lose}</p>
        </div>
        <button className="btn btn-primary" onClick={reset}>Reset score</button>
      </div>

      <div className="modal" tabindex="-1">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Modal title</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <p>Modal body text goes here.</p>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" className="btn btn-primary">Save changes</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
