import './App.css';
import rock from './rock.png';
import paper from './paper.png';
import scissors from './scissors.png';
import { useState } from 'react';


function App() {
  const [player, setPlayer] = useState("");
  const [computer, setComputer] = useState("");
  const [status, setStatus] = useState("");

  function click(e) {
    setPlayer(e.target.src);
    let computerRoll = Math.floor(Math.random() * 3 + 1);
    let playerRoll = 0;
    if (e.target.src.includes("rock")) {
      playerRoll = 1;
    }
    if (e.target.src.includes("paper")) {
      playerRoll = 2;
    }
    if (e.target.src.includes("scissors")) {
      playerRoll = 3;
    }

    if (computerRoll == 1) {
      setComputer(rock)
    }
    if (computerRoll == 2) {
      setComputer(paper)
    }
    if (computerRoll  == 3) {
      setComputer(scissors)
    }

    if (playerRoll == computerRoll) {
      setStatus("DRAW");
    } else if (playerRoll == 1 && computerRoll == 2) {
      setStatus("LOSE");
    } else if (playerRoll == 1 && computerRoll == 3) {
      setStatus("WIN");
    } else if (playerRoll == 2 && computerRoll == 1) {
      setStatus("WIN");
    }else if (playerRoll == 2 && computerRoll == 3) {
      setStatus("LOSE");
    } else if (playerRoll == 3 && computerRoll == 1) {
      setStatus("LOSE");
    }else if (playerRoll == 3 && computerRoll == 2) {
      setStatus("WIN");
    }
  }

  return (
    <div className="App">
      <h1 className="status">{status}</h1>
      <div className="container">

        <div className="d-flex justify-content-between mt-5 play">
          <div className="player">
            <p className="player-name">you</p>
            <img src={player} alt="" className="player-choice"></img>
          </div>
          <div className="player">
            <p className="player-name">computer</p>
            <img src={computer} alt="" className="player-choice"></img>
          </div>
        </div>

        <div className="d-flex justify-content-between mt-5">
          <img src={rock} alt="" className="player-choice" onClick={click}></img>
          <img src={paper} alt="" className="player-choice" onClick={click}></img>
          <img src={scissors} alt="" className="player-choice" onClick={click}></img>
        </div>
      </div>

    </div>
  );
}

export default App;
