import './App.css';
import rock from './rock.png';
import paper from './paper.png';
import scissors from './scissors.png';
import { useState } from 'react';

function App() {
  const [player, setPlayer] = useState(scissors);
  const [computer, setComputer] = useState(scissors);
  // const [status, setStatus] = useState("Welcome");
  const [win, setWin] = useState(0);
  const [draw, setDraw] = useState(0);
  const [lose, setLose] = useState(0);
  const [total, setTotal] = useState(0);
  const [overLay, setOverLay] = useState("overLay");

  function click(playerRoll) {
    if (playerRoll === 1) {
      setPlayer(rock);
    }
    if (playerRoll === 2) {
      setPlayer(paper);
    }
    if (playerRoll === 3) {
      setPlayer(scissors);
    }

    let computerRoll = Math.floor(Math.random() * 3 + 1);

    if (computerRoll === 1) {
      setComputer(rock)
    }
    if (computerRoll === 2) {
      setComputer(paper)
    }
    if (computerRoll  === 3) {
      setComputer(scissors)
    }

    if (playerRoll === computerRoll) {
      // setStatus("DRAW");
      setDraw(draw + 1);
      setTotal(total + 1);
    }
    else if (playerRoll === 1 && computerRoll === 2) {
      // setStatus("LOSE");
      setLose(lose + 1);
      setTotal(total + 1);
    }
    else if (playerRoll === 1 && computerRoll === 3) {
      // setStatus("WIN");
      setWin(win + 1);
      setTotal(total + 1);
    }
    else if (playerRoll === 2 && computerRoll === 1) {
      // setStatus("WIN");
      setWin(win + 1);
      setTotal(total + 1);
    }
    else if (playerRoll === 2 && computerRoll === 3) {
      // setStatus("LOSE");
      setLose(lose + 1);
      setTotal(total + 1);
    }
    else if (playerRoll === 3 && computerRoll === 1) {
      // setStatus("LOSE");
      setLose(lose + 1);
      setTotal(total + 1);
    }
    else if (playerRoll === 3 && computerRoll === 2) {
      // setStatus("WIN");
      setWin(win + 1);
      setTotal(total + 1);
    }
    // let total = win + lose + draw;
  }
  if (total === 10) {
    if (win === lose) {
      setOverLay("overLay visible");

    }
    else if (win > lose) {
      alert("You win")
    }
    else alert("You lose")
    setWin(0);
    setLose(0);
    setDraw(0);
  }


  let reset = () => {
    setWin(0);
    setLose(0);
    setDraw(0);
  }
  return (
    <div className="App">
      <div className={overLay}>
        <div className="card" style={{width: '18rem'}}>
          <div className="card-header">
            <h2>You win</h2>
          </div>
          <div className="card-body">
            <p className="card-text">Win: 0 - Draw: 0 - Lose: 0</p>
            <a href="#" className="btn btn-primary">Play again</a>
          </div>
        </div>
      </div>
      <h1 className="status">Rock paper scissors</h1>
      <div className="container">
        <div className="d-flex justify-content-between play">
          <div className="player me-5">
            <h2 className="player-name ">You</h2>
            <img src={player} alt="" className="player-choice"></img>
          </div>
          <div className="player">
            <h2 className="player-name">Computer</h2>
            <img src={computer} alt="" className="computer-choice"></img>
          </div>
        </div>
        <div className="d-flex justify-content-between mt-5">
          <img src={rock} alt="" className="choices" onClick={() => click(1)}></img>
          <img src={paper} alt="" className="choices" onClick={() => click(2)}></img>
          <img src={scissors} alt="" className="choices" onClick={() => click(3)}></img>
        </div>
        <div className="d-flex justify-content-between mt-3">
          <h2 className="score">Win: {win}</h2>
          <h2 className="score">Draw: {draw}</h2>
          <h2 className="score">Lose: {lose}</h2>
        </div>
        <button className="btn btn-primary" onClick={reset}>Reset score</button>
      </div>
    </div>
  );
}

export default App;
